import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [name, setName] = useState('');

  const handleImage =() => {
    setName('umar faruk')
  }

  useEffect(() => {
    console.log('before setTimeOut');
     setTimeout(handleImage, 3000);

  }, []);


  return (
    <div className="App">
      {
        name ? '' : <img src={'https://media.geeksforgeeks.org/wp-content/uploads/20190617082250/jshd2a.png'} alt="" />
      }

      <button onClick={handleImage}>button</button>
    </div>
  );
}

export default App;
