  const handleImage =() => {
    setName('umar faruk')
  }

  useEffect(() => {
    console.log('before setTimeOut');
     setTimeout(handleImage, 3000);

    // console.log('my time', timer);
    // return () => clearTimeout(timer);
  }, []);


  return (
    <div className="App">
      {
        name ? <h1>nothing</h1> : <h1>my image is here</h1>
      }

      <button onClick={handleImage}>button</button>
    </div>
  );
}

export default App;
